const colorBtns = document.querySelectorAll('.color-btn');
const body = document.querySelector('.body');
const bodyBefore = document.querySelector('.body::before');
const legLeft = document.querySelector('.leg-left');
const legRight = document.querySelector('.leg-right');
const bag = document.querySelector('.bag');
const bagBefore = document.querySelector('.bag::before');

// 设置默认激活颜色
colorBtns[4].classList.add('active'); // 橙色为默认

colorBtns.forEach(btn => {
  btn.addEventListener('click', function() {
    // 移除所有激活状态
    colorBtns.forEach(b => b.classList.remove('active'));
    this.classList.add('active');

    const mainColor = this.dataset.main;
    const lightColor = this.dataset.light;

    // 更改身体颜色
    body.style.backgroundColor = mainColor;
    legLeft.style.backgroundColor = mainColor;
    legRight.style.backgroundColor = mainColor;

    // 更改背包和身体前景色
    bag.style.backgroundColor = lightColor;

    // 通过CSS变量来更改伪元素颜色
    document.documentElement.style.setProperty('--body-light-color', lightColor);
    document.documentElement.style.setProperty('--bag-dark-color', mainColor);

    // 直接修改样式
    const style = document.createElement('style');
    style.textContent = `
      .body::before { background-color: ${lightColor} !important; }
      .bag::before { background: ${mainColor} !important; }
    `;

