body {
  background: #0b0c10;
  margin: 0;
  overflow: hidden;
  font-family: 'Segoe UI', sans-serif;
}

#particleCanvas {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 0;
  pointer-events: none;
}

.slider-container {
  position: absolute;
  bottom: 50%;
  left: 50%;
  transform: translateX(-50%) translateY(-50%);
  width: 80%;
  max-width: 500px;
  z-index: 2;
}

input[type=range] {
  width: 100%;
  height: 16px;
  appearance: none;
  background: linear-gradient(90deg, gold, orange);
  border-radius: 8px;
  outline: none;
  box-shadow: 0 0 15px gold;
}

input[type=range]::-webkit-slider-thumb {
  appearance: none;
  width: 26px;
  height: 26px;
  background: white;
  border-radius: 50%;
  box-shadow: 0 0 10px gold, 0 0 30px orange;
  transition: transform 0.2s ease;
}

input[type=range]:active::-webkit-slider-thumb {
  transform: scale(1.2);
}

.value-bubble {
  position: absolute;
  top: -50px;
  left: 50%;
  transform: translateX(-50%);
  background: gold;
  color: black;
  padding: 8px 14px;
  border-radius: 20px;
  font-size: 18px;
  font-weight: bold;
  box-shadow: 0 0 10px gold;
  pointer-events: none;
}

